import React from "react";
import { Container, Row, Col, Card, Button, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { FiShoppingBag, FiTool, FiHome } from "react-icons/fi";

export default function Home() {
  const nav = useNavigate();
  const options = [
    {
      title: "Buy / Rent / Sell",
      path: "/market",
      variant: "success",
      icon: FiShoppingBag,
    },
    { title: "Hire Workers", path: "/workers", variant: "info", icon: FiTool },
    {
      title: "Rent / Get a Room",
      path: "/rooms",
      variant: "warning",
      icon: FiHome,
    },
  ];

  return (
    <>
      <Container
        fluid
        style={{ background: "linear-gradient(135deg, #4f8bc9, #7bbfea)" }}
      >
        <motion.h1
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="display-4"
        >
          Welcome to Rentalo
        </motion.h1>
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4, duration: 0.6 }}
          className="lead"
        >
          Your all-in-one rental marketplace
        </motion.p>
      </Container>

      <Container className="my-5">
        <Row xs={1} md={3} className="g-4">
          {options.map((opt) => (
            <Col key={opt.title}>
              <motion.div
                whileHover={{ scale: 1.05 }}
                transition={{ duration: 0.3 }}
              >
                <Card className="h-100 text-center shadow-lg border-0">
                  <Card.Body>
                    <Card.Title>{opt.title}</Card.Title>
                    <Button
                      variant={opt.variant}
                      onClick={() => nav(opt.path)}
                      className="mt-3"
                    >
                      Explore
                    </Button>
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
}
